import *as THREE from 'three'
import { rotate } from 'three/examples/jsm/nodes/Nodes.js';

const toono = () => {

    const outerCirculerRadius = 60;
    const outerTube = 5;
    const hvree = new THREE.TorusGeometry(outerCirculerRadius, outerTube,10,100);
    const Hvreemesh = new THREE.Mesh(hvree, new THREE.MeshLambertMaterial({color: 0xff6600}));

    const tolgoi = new THREE.TorusGeometry(outerCirculerRadius/5, outerTube/2,10,10);
    const tolgoimesh = new THREE.Mesh(tolgoi, new THREE.MeshLambertMaterial({color: 0x006600, side: THREE.DoubleSide}));
    tolgoimesh.position.z = 5;
    const combinedToono = new THREE.Group();
    
    combinedToono.add(Hvreemesh);
    combinedToono.add(tolgoimesh);
    
    return combinedToono;
}
export {toono};